import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';
import { useState } from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.css'
import './common.css'
import { Link } from 'react-router-dom/cjs/react-router-dom.min';

function Login() {
    const [Credentials,setCredentials]=useState({email:"",Password:""});
    
    const onTextChange=(args)=>
    {
        var copyofCredentials={...Credentials};
        copyofCredentials[args.target.name]=args.target.value;
        setCredentials(copyofCredentials);
    }

    const history=useHistory();
    const SignIn=()=>{
        var helper=new XMLHttpRequest();
        helper.onreadystatechange=()=>{

            if(helper.readyState==4 && helper.status==200)
            {
                var responsereceived=JSON.parse(helper.responseText);
                responsereceived.map((checkCredentials)=>{
                    console.log(checkCredentials)
                    if(checkCredentials.email==Credentials.email && checkCredentials.password==Credentials.Password)
                    {
                        window.sessionStorage.setItem("isLoggedIn","true");
                        window.sessionStorage.setItem("user_id",checkCredentials.id);
                        history.push("/Quotes");
                    }
                    else
                    {
                        console.log("Wrong credentials");
                    }
                })
                
            }

        }
        helper.open("GET","http://127.0.0.1:9999/Login");
        helper.setRequestHeader("Content-Type","application/json");
        helper.send();
    }

    return (
        <body className='background'>
        <div >
            <center>
        <div className='registerDetails'>
        
        <center>
            <table className='table-responsive'>
                <tr>
                    <td>
                        UserName:
                    </td>
                    <td>
                        <input type='text' onChange={onTextChange} value={Credentials.email} name='email'></input>
                    </td>
                </tr>
                <br/>
                <tr>
                    <td>
                        Password:
                    </td>
                    <td>
                    <input type='password' onChange={onTextChange} value={Credentials.Password} name='Password'></input>
                  </td>
                </tr><br/>
                <tr style={a}>
                    <td colSpan={2}>
                        <button className='loginButton' onClick={SignIn}>
                            Login
                        </button><br/><br/>
                        Dont Have Account <Link to={"/Register"}>Register Here</Link>
                    </td>
                </tr>
            </table>
            </center>
            </div>
            </center>
            </div>
            </body>);
}

export default Login;
